import React, { useState, useEffect } from 'react';
import { getBookings, postBooking, deleteBooking,updateBookings } from '../coponents/bookingsService';
import BookingsList from '../coponents/BookingsList';


const BookingContainer = () => {
    const [bookings, setBookings] = useState([]);

    useEffect(() => {
        getBookings().then((data) => setBookings(data))

    }, [])

    const onDelete = async (id) => {
        // console.log('we top now id is :::' + id)
        await deleteBooking(id)

        const bookingsCopy = [...bookings]
        const filteredArry = bookingsCopy.filter((booking) => booking._id != id)
        setBookings(filteredArry)
    }

    const onChangeCheckInStatus = async  (id) => {

        const bookingToChange = bookings.filter((booking) => booking._id === id)

        bookingToChange[0].checked_in=!bookingToChange[0].checked_in
        const  bookingToApi=[...bookingToChange]
        delete bookingToApi[0]._id
await updateBookings(id,bookingToApi[0])
        const bookingsCopy=[...bookings]
        const filteredArray=bookingsCopy.filter((booking) => booking._id != id)
        filteredArray.push(bookingToChange[0])
        // const  bookingToApi=[...bookingToChange]
        // delete bookingToApi[0]._id
        // console.log(bookingToApi[0])
        // await updateBookings(id,bookingToApi[0])
        // await setBookings(filteredArray)

    }





    return (
        <>
            <BookingsList bookings={bookings} onDelete={onDelete} onChangeCheckInStatus={onChangeCheckInStatus} />


        </>

    )


};

export default BookingContainer;
